import{o as t,c as d,a as e}from"./app.2bd60a78.js";const a='{"title":"Modal 模态框弹窗","description":"","frontmatter":{},"headers":[{"level":2,"title":"Dialog.showDialog(object)","slug":"dialog-showdialog-object"},{"level":3,"title":"Params","slug":"params"},{"level":3,"title":"Usage","slug":"usage"}],"relativePath":"apis/Modal.md","lastUpdated":1624584443316}',r={},o=e('<h1 id="modal-模态框弹窗"><a class="header-anchor" href="#modal-模态框弹窗" aria-hidden="true">#</a> Modal 模态框弹窗</h1><h2 id="dialog-showdialog-object"><a class="header-anchor" href="#dialog-showdialog-object" aria-hidden="true">#</a> Dialog.showDialog(object)</h2><p>显示模态对话框。</p><h3 id="params"><a class="header-anchor" href="#params" aria-hidden="true">#</a> Params</h3><p>Object object</p><table><thead><tr><th>属性</th><th>类型</th><th>默认值</th><th>必填</th><th>说明</th></tr></thead><tbody><tr><td>title</td><td>string</td><td></td><td>否</td><td>提示的标题</td></tr><tr><td>content</td><td>string</td><td></td><td>是</td><td>提示的内容</td></tr><tr><td>maskClosable</td><td>boolean</td><td>false</td><td>否</td><td>点击蒙层是否触发关闭操作</td></tr><tr><td>showCancel</td><td>boolean</td><td>true</td><td>否</td><td>是否显示取消按钮</td></tr><tr><td>confirmText</td><td>string</td><td>&#39;确定&#39;</td><td>否</td><td>确认按钮的文字</td></tr><tr><td>cancelText</td><td>string</td><td>&#39;取消&#39;</td><td>否</td><td>取消按钮的文字</td></tr><tr><td>success</td><td>function</td><td></td><td>否</td><td>接口调用成功（在用户做出选择后，如取消，确定）的回调函数</td></tr><tr><td>fail</td><td>function</td><td></td><td>否</td><td>接口调用失败（如传入错误的参数）的回调函数（不传入 fail 遇错误直接抛出）</td></tr><tr><td>complete</td><td>function</td><td></td><td>否</td><td>弹窗关闭或调用失败的回调函数</td></tr></tbody></table><h4 id="object-success-回调参数"><a class="header-anchor" href="#object-success-回调参数" aria-hidden="true">#</a> object.success 回调参数</h4><table><thead><tr><th>属性</th><th>类型</th><th>说明</th></tr></thead><tbody><tr><td>confirm?</td><td>boolean</td><td>为 true 时，表示用户点击了确定按钮</td></tr><tr><td>cancel?</td><td>boolean</td><td>为 true 时，表示用户取消了</td></tr></tbody></table><h3 id="usage"><a class="header-anchor" href="#usage" aria-hidden="true">#</a> Usage</h3><div class="language-"><pre><code>import { Dialog } from &#39;vfox&#39;\n\nDialog.showDialog({\n  title: &#39;提示&#39;,\n  content: &#39;这是一个模态弹窗&#39;,\n  success (res) {\n    if (res.confirm) {\n      console.log(&#39;用户点击确定&#39;)\n    } else if (res.cancel) {\n      console.log(&#39;用户点击取消&#39;)\n    }\n  }\n})\n</code></pre></div>',10);r.render=function(e,a,r,s,l,h){return t(),d("div",null,[o])};export default r;export{a as __pageData};
